import React, { useContext, useState } from 'react';
import { CartContext } from "../store/shopping-cart-context";
import { groupAttributesBySet } from '../utilities/attributeUtils';
import { ToastContainer, toast } from 'react-toastify';
import AttributeSet from './AttributeSet';
import parse from 'html-react-parser';

export default function Product({ product }) {
  const { addItemToCart, handleCheckboxChange, allAttributesSelected, selectedAttributes } = useContext(CartContext);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  if (!product) {
    return <div>Loading...</div>;
  }

  const groupedAttributes = groupAttributesBySet(product.attributes) || {};
  const isAddToCartDisabled = !allAttributesSelected(groupedAttributes) || !product.inStock;

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % product.images.length);
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + product.images.length) % product.images.length);
  };

  return (
    <div id="product">
      <div className="gallery">
        <div className="all-images">
          {product.images && product.images.map((image, index) => (
            <img key={index} src={image.image_url} alt={`${product.name} ${index + 1}`} onClick={() => setCurrentImageIndex(index)} />
          ))}
        </div>
        <div className="main-image">
          <img src={product.images[currentImageIndex].image_url} alt={`${product.name} main`} />
          <button className="prev-arrow" onClick={handlePrevImage}>&#9664;</button>
          <button className="next-arrow" onClick={handleNextImage}>&#9654;</button>
        </div>
      </div>
      <div className="other-details">
        <h3>{product.name}</h3>
        <p>{parse(product.description)}</p>
        {Object.entries(groupedAttributes).map(([setName, attributes]) => (
          <AttributeSet
            key={setName}
            setName={setName}
            attributes={attributes}
            selectedAttributes={selectedAttributes}
            handleCheckboxChange={handleCheckboxChange}
          />
        ))}
        <p className="price">Price: <br /><span>{product.price.currency.symbol}{product.price.amount}</span></p>
        <button className="add-to-cart-button" disabled={isAddToCartDisabled} onClick={() => addItemToCart(product, selectedAttributes)}>{product.inStock ? 'Add to cart' : 'Out of Stock'}</button>
      </div>
    </div>
  );
}
