import React, { useContext, useState, useEffect } from 'react';
import { CartContext } from "../store/shopping-cart-context";
import { groupAttributesBySet } from '../utilities/attributeUtils';
import AttributeSet from './AttributeSet';

export default function Product({ product }) {
  const { addItemToCart, handleCheckboxChange, allAttributesSelected, selectedAttributes } = useContext(CartContext);

  const [localSelectedAttributes, setLocalSelectedAttributes] = useState({});

  useEffect(() => {
    setLocalSelectedAttributes(selectedAttributes);
  }, [selectedAttributes]);

  if (!product) {
    return <div>Loading...</div>;
  }

  const groupedAttributes = groupAttributesBySet(product.attributes) || {};

  const handleLocalCheckboxChange = (setName, attrId) => {
    setLocalSelectedAttributes((prevState) => ({
      ...prevState,
      [setName]: attrId,
    }));
  };

  const handleAddToCart = () => {
    addItemToCart(product.id, localSelectedAttributes);
    setLocalSelectedAttributes({});
  };

  return (
    <div id="product">
      <div className="gallery">
        <div className="all-images">
          {product.images && product.images.map((image, index) => (
            <img key={index} src={image.image_url} alt={`${product.name} ${index + 1}`} />
          ))}
        </div>
        <div className="main-image">
          <img src={product.images[0].image_url} alt={`${product.name} main`} />
        </div>
      </div>
      <div className="other-details">
        <h3>{product.name}</h3>
        <div dangerouslySetInnerHTML={{ __html: product.description }} />
        {Object.entries(groupedAttributes).map(([setName, attributes]) => (
          <AttributeSet
            key={setName}
            setName={setName}
            attributes={attributes}
            selectedAttributes={localSelectedAttributes}
            handleCheckboxChange={handleLocalCheckboxChange}
          />
        ))}
        <p className="price">Price: <br /><span>{product.price.currency.symbol}{product.price.amount}</span></p>
        <button className="add-to-cart-button" disabled={!allAttributesSelected(groupedAttributes)} onClick={handleAddToCart}>Add to cart</button>
      </div>
    </div>
  );
};
