import React from 'react';

export default function Product({ product }) {
  if (!product) {
    return <div>Loading...</div>;
  }

  function groupAttributesBySet(attributes) {
    return attributes.reduce((acc, attribute) => {
        const setName = attribute.attribute_set.name;
        if (!acc[setName]) {
            acc[setName] = [];
        }
        acc[setName].push(attribute);
        return acc;
    }, {});
  }

  const groupedAttributes = groupAttributesBySet(product.attributes)

  return (
    <div id="product">
      <div className="gallery">
        <div className="all-images">
          {product.images && product.images.map((image, index) => (
            <img key={index} src={image.image_url} alt={`${product.name} image ${index + 1}`} />
          ))}
        </div>
        <div className="main-image">
          <img src={product.images[0].image_url} alt={`${product.name} main image`} />
        </div>
      </div>
      <div className="other-details">
        <h3>{product.name}</h3>
        <div dangerouslySetInnerHTML={{ __html: product.description }} />
        {Object.entries(groupedAttributes).map(([setName, attributes]) => (
          <div className="attribute-set" key={setName}>
            <h3 className="attribute-set-name">{setName}</h3>
            <div className="attributes">
              {attributes.map(attr => (
                <div key={attr.id} className="attribute">
                  <div className="inner-attribute">{attr.display_value}</div>
                </div>
              ))}
            </div>
          </div>
        ))}
        <p className="price">Price: <br/><span>{product.price.currency.symbol}{product.price.amount}</span></p>
      </div>
    </div>
  );
}
