import React from 'react';

export default function Product({ product }) {
  const { product_name, description, brand, category_name, price_amount, currency_symbol, gallery_image_url, attributes } = product.product;

  return (
    <div className="product">
      <div className="gallery">
        <div className="all-images">
          {product.images && product.images.map((url, index) => (
            <img key={index} src={url} alt={`${product_name} image ${index + 1}`} />
          ))}
        </div>
        <div className="main-image">
          <img src={gallery_image_url} alt={`${product_name} main image`} />
        </div>
      </div>
      <div className="other-details">
        <h3>{product_name}</h3>
        <p>{description}</p>
        <p>Brand: {brand}</p>
        <p>Category: {category_name}</p>
        <p>Price: {currency_symbol}{price_amount}</p>
        <div className="attributeSets">
          {Object.keys(attributes).map((key) => {
            const attributeSet = attributes[key];
            return (
              <div className="attributeSet" key={key}>
                <p>{attributeSet.attribute_set_name}</p>
                <div className="attributes">
                  {attributeSet.attributes.map((attribute, index) => (
                    <div className="attribute" key={index}>
                      {attribute.attribute_value}
                    </div>
                  ))}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
