import React from 'react';
import { groupAttributesBySet } from '../utilities/attributeUtils';

function CartItem({ item }) {
  const groupedAttributes = groupAttributesBySet(item.attributes);
  console.log(groupedAttributes);
  return (
    // <div className="cart-item">
    //   <img src={item.images[0].image_url} alt={item.name} />
    //   <div className="item-details">
    //     <h4>{item.name}</h4>
    //     <p>Price: {item.price.currency.symbol}{item.price.amount}</p>
    //     <div className="item-attributes">
    //       {item.attributes.map((attr) => (
    //         <div key={attr.id} className="attribute">
    //           <span className="attribute-name">{attr.attribute_set.name}: </span>
    //           <span
    //             className={`attribute-value ${
    //               item.selectedAttributes[attr.attribute_set.name] === attr.id ? 'selected' : ''
    //             }`}
    //           >
    //             {attr.display_value}
    //           </span>
    //         </div>
    //       ))}
    //     </div>
    //     <p>Quantity: {item.quantity}</p>
    //   </div>
    // </div>

    <div className="cart-item">
      <div className="item-details">
        <p className="item-name">{item.name}</p>
        <p className="item-price">{item.price.currency.symbol}{item.price.amount}</p>
        <div className="item-attributes">
          {groupedAttributes.map((attr) => (
            <div key={attr.id} className="attribute">
              <span className="attribute-name">{attr.attribute_set.name}: </span>
              <span
                className={`attribute-value ${
                  item.selectedAttributes[attr.attribute_set.name] === attr.id ? 'selected' : ''
                }`}
              >
                {attr.display_value}
              </span>
            </div>
          ))}
        </div>
      </div>
      <div className="cart-item-actions">
        <button>+</button>
        <p>{item.quantity}</p>
        <button>-</button>
      </div>
      <div className="image">
        <img src={item.images[0].image_url} />
      </div>
    </div>
  );
}

export default CartItem;
