import { createContext, useReducer } from 'react';
import { gql, useLazyQuery } from '@apollo/client';

export const CartContext = createContext({
  items: [],
  selectedAttributes: {},
  addItemToCart: () => {},
  updatedItemQuantity: () => {},
  handleCheckboxChange: () => {},
  allAttributesSelected: () => false,
});

const GET_PRODUCT_BY_ID = gql`
  query GetProductById($id: String!) {
    product(id: $id) {
      id
      name
      inStock
      description
      price {
        amount
        currency {
          symbol
        }
      }
      images {
        image_url
      }
      attributes {
        id
        value
        display_value
        attribute_set {
          name
        }
      }
    }
  }
`;

function shoppingCartReducer(state, action) {
  switch (action.type) {
    case 'ADD_ITEM':
      const updatedItems = [...state.items];
      const existingCartItemIndex = updatedItems.findIndex(
        (cartItem) =>
          cartItem.id === action.payload.id &&
          JSON.stringify(cartItem.attributes) === JSON.stringify(action.payload.attributes)
      );
      const existingCartItem = updatedItems[existingCartItemIndex];

      if (existingCartItem) {
        const updatedItem = {
          ...existingCartItem,
          quantity: existingCartItem.quantity + 1,
        };
        updatedItems[existingCartItemIndex] = updatedItem;
      } else {
        updatedItems.push({
          id: action.payload.id,
          name: action.payload.name,
          price: action.payload.price,
          images: action.payload.images,
          attributes: action.payload.attributes,
          quantity: 1,
        });
      }

      return {
        ...state,
        items: updatedItems,
      };

    case 'UPDATE_ITEM':
      const updatedItemsForUpdate = [...state.items];
      const updatedItemIndex = updatedItemsForUpdate.findIndex(
        (item) => item.id === action.payload.productId
      );
      const updatedItem = {
        ...updatedItemsForUpdate[updatedItemIndex],
      };

      updatedItem.quantity += action.payload.amount;

      if (updatedItem.quantity <= 0) {
        updatedItemsForUpdate.splice(updatedItemIndex, 1);
      } else {
        updatedItemsForUpdate[updatedItemIndex] = updatedItem;
      }

      return {
        ...state,
        items: updatedItemsForUpdate,
      };

    case 'SET_SELECTED_ATTRIBUTES':
      return {
        ...state,
        selectedAttributes: {
          ...state.selectedAttributes,
          [action.payload.setName]: action.payload.attrId,
        },
      };

    case 'RESET_SELECTED_ATTRIBUTES':
      return {
        ...state,
        selectedAttributes: {},
      };

    default:
      return state;
  }
}

export default function CartContextProvider({ children }) {
  const [shoppingCartState, shoppingCartDispatch] = useReducer(shoppingCartReducer, {
    items: [],
    selectedAttributes: {},
  });

  const [fetchProduct] = useLazyQuery(GET_PRODUCT_BY_ID, {
    onCompleted: (data) => {
      shoppingCartDispatch({
        type: 'ADD_ITEM',
        payload: {
          ...data.product,
          attributes: shoppingCartState.selectedAttributes,
        },
      });
      shoppingCartDispatch({
        type: 'RESET_SELECTED_ATTRIBUTES',
      });
    },
  });

  function handleAddItemToCart(id) {
    fetchProduct({ variables: { id } });
  }

  function handleUpdateCartItemQuantity(productId, amount) {
    shoppingCartDispatch({
      type: 'UPDATE_ITEM',
      payload: {
        productId,
        amount,
      },
    });
  }

  function handleCheckboxChange(setName, attrId) {
    shoppingCartDispatch({
      type: 'SET_SELECTED_ATTRIBUTES',
      payload: {
        setName,
        attrId,
      },
    });
  }

  function allAttributesSelected(groupedAttributes) {
    return Object.keys(groupedAttributes).every(
      (setName) => shoppingCartState.selectedAttributes[setName]
    );
  }

  const ctxValue = {
    items: shoppingCartState.items,
    selectedAttributes: shoppingCartState.selectedAttributes,
    addItemToCart: handleAddItemToCart,
    updatedItemQuantity: handleUpdateCartItemQuantity,
    handleCheckboxChange: handleCheckboxChange,
    allAttributesSelected: allAttributesSelected,
  };

  return (
    <CartContext.Provider value={ctxValue}>
      {children}
    </CartContext.Provider>
  );
}
