import React, { useState } from 'react';

import { CartContext } from "../store/shopping-cart-context";

export default function Product({ product }) {
  const [selectedAttributes, setSelectedAttributes] = useState({});

  if (!product) {
    return <div>Loading...</div>;
  }

  // Function to group attributes by their set
  function groupAttributesBySet(attributes) {
    return attributes.reduce((acc, attribute) => {
      const setName = attribute.attribute_set.name;
      if (!acc[setName]) {
        acc[setName] = [];
      }
      acc[setName].push(attribute);
      return acc;
    }, {});
  }

  const groupedAttributes = groupAttributesBySet(product.attributes);

  // Handler for checkbox change
  const handleCheckboxChange = (setName, attrId) => {
    setSelectedAttributes(prevState => ({
      ...prevState,
      [setName]: attrId
    }));
  };

  const allAttributesSelected = () => {
    return Object.keys(groupedAttributes).every(setName => selectedAttributes[setName]);
  };

  return (
    <div id="product">
      <div className="gallery">
        <div className="all-images">
          {product.images && product.images.map((image, index) => (
            <img key={index} src={image.image_url} alt={`${product.name} ${index + 1}`} />
          ))}
        </div>
        <div className="main-image">
          <img src={product.images[0].image_url} alt={`${product.name} main`} />
        </div>
      </div>
      <div className="other-details">
        <h3>{product.name}</h3>
        <div dangerouslySetInnerHTML={{ __html: product.description }} />
        {Object.entries(groupedAttributes).map(([setName, attributes]) => (
          <div className="attribute-set" key={setName}>
            <h3 className="attribute-set-name">{setName}</h3>
            <div className="attributes">
              {attributes.map(attr => (
                <div
                  key={attr.id}
                  className="attribute"
                >
                  <label className={`inner-attribute ${selectedAttributes[setName] === attr.id ? 'selected' : ''}`}>
                    <input
                      type="checkbox"
                      checked={selectedAttributes[setName] === attr.id}
                      onChange={() => handleCheckboxChange(setName, attr.id)}
                    />
                    {attr.display_value}
                  </label>
                </div>
              ))}
            </div>
          </div>
        ))}
        <p className="price">Price: <br /><span>{product.price.currency.symbol}{product.price.amount}</span></p>
        <button disabled={!allAttributesSelected()} onClick={() => addItemToCart(product.id)}>Add to cart</button>
      </div>
    </div>
  );
};
