import { forwardRef, useImperativeHandle, useRef, useContext, useState } from 'react';
import { createPortal } from 'react-dom';
import { gql, useQuery } from '@apollo/client';
import Cart from './Cart';
import { CartContext } from '../store/shopping-cart-context';

const CREATE_ORDER = gql`
  mutation CreateOrder($input: OrderInput!) {
    createOrder(input: $input) {
      id
      total
      items {
        id
        product_id
        quantity
        price
        attributes {
          id
          attribute_id
        }
      }
    }
  }
`;

const CartModal = forwardRef(function Modal(_, ref) {
  const modalRef = useRef();
  const [isOpen, setIsOpen] = useState(false);
  const { items, clearCart } = useContext(CartContext);

  const [createOrder, { loading, error }] = useMutation(CREATE_ORDER, {
    onCompleted: (data) => {
      console.log('Order created:', data);
      clearCart();
      setIsOpen(false);
    },
  });

  useImperativeHandle(ref, () => ({
    open: () => setIsOpen(true),
    close: () => setIsOpen(false),
  }));

  if (!isOpen) {
    return null;
  }

  return createPortal(
    <>
      <div className="modal-backdrop" onClick={() => setIsOpen(false)} />
      <div className="modal" ref={modalRef}>
        <Cart />
        <div id="modal-actions">
          <button disabled={items.length < 1} className="place-order-button">Place order</button>
        </div>
      </div>
    </>,
    document.getElementById('modal-root')
  );
});

export default CartModal;
