import { useLoaderData } from 'react-router-dom';

const HomePage = () => {

    const {products} = useLoaderData();

    return (
        <div id="homepage">
            <h2>All products</h2>
            <div className="products-container">
                <div className="product">
                    <div className="inner-product">
                        <img src="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" alt="product-image"/>
                        <div className="product-info">
                            <p className="product-name">Nike Air Huarache Le</p>
                            <p className="product-price">$50.00</p>
                        </div>
                    </div>
                </div>
                <div className="product">
                    <div className="inner-product">
                        <img src="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" alt="product-image"/>
                        <div className="product-info">
                            <p className="product-name">Nike Air Huarache Le</p>
                            <p className="product-price">$50.00</p>
                        </div>
                    </div>
                </div>
                <div className="product">
                    <div className="inner-product">
                        <img src="https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" alt="product-image"/>
                        <div className="product-info">
                            <p className="product-name">Nike Air Huarache Le</p>
                            <p className="product-price">$50.00</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default HomePage;

async function loadProducts() {
    const response = await fetch('http://localhost/ecommerce/backend/getAllProducts.php');
  
    if (!response.ok) {
      // Handle error case
      throw new Error('Could not fetch events');
    }
  
    const resData = await response.json();
    return resData.events;
  }
  
  export async function loader() {
    try {
      const products = await loadProducts();
      return { products };
    } catch (error) {
      return { isError: true, message: error.message };
    }
  }
