import { forwardRef, useImperativeHandle, useRef, useContext, useState } from 'react';
import { createPortal } from 'react-dom';
import Cart from './Cart';
import { CartContext } from '../store/shopping-cart-context';

const CartModal = forwardRef(function Modal(_, ref) {
  const modalRef = useRef();
  const [isOpen, setIsOpen] = useState(false);

  useImperativeHandle(ref, () => ({
    open: () => setIsOpen(true),
    close: () => setIsOpen(false),
  }));

  const { items } = useContext(CartContext);

  if (!isOpen) {
    return null;
  }

  return createPortal(
    <>
      <div className="modal-backdrop" onClick={() => setIsOpen(false)} />
      <div className="modal" ref={modalRef}>
        <Cart />
        <div id="modal-actions">
          <button disabled={items.length < 1} className="place-order-button">Place order</button>
        </div>
      </div>
    </>,
    document.getElementById('modal-root')
  );
});

export default CartModal;
