import React, { useContext } from 'react';

import { CartContext } from '../store/shopping-cart-context';
import { groupAttributesBySet } from '../utilities/attributeUtils';

function Cart() {
    const { items, updatedItemQuantity } = useContext(CartContext);
    console.log(items)

    const totalPrice = items.reduce(
        (acc, item) => acc + item.price.amount * item.quantity, 0
    );
    
    return (
        <div id="cart">
            {items.length === 0 && <p>No items in cart!</p>}
            {items.length > 0 && (
                <ul id="cart-items">
                {items.map((item) => {
                    const groupedAttributes = groupAttributesBySet(item.attributes)
                    return (
                        <li key={item.id}>
                            <div className="item-details">
                                <p className="item-name">{item.name}</p>
                                <p className="item-price">{item.price.currency.symbol}{item.price.amount}</p>
                                {Object.entries(groupedAttributes).map(([setName, attributes]) => (
                                    <div className="attribute-set" key={setName}>
                                        <h3 className="attribute-set-name">{setName}</h3>
                                        <div className="attributes">
                                        {attributes.map(attr => (
                                            <div
                                            key={attr.id}
                                            className="attribute"
                                            >
                                            <label className={`inner-attribute ${selectedAttributes[setName] === attr.id ? 'selected' : ''}`}>
                                                <input
                                                type="checkbox"
                                                checked={selectedAttributes[setName] === attr.id}
                                                onChange={() => handleCheckboxChange(setName, attr.id)}
                                                />
                                                {attr.display_value}
                                            </label>
                                            </div>
                                        ))}
                                        </div>
                                    </div>
                                ))}
                            </div>
                            <div className="cart-item-actions">
                                <button onClick={() => updatedItemQuantity(item.id, -1)}>
                                    -
                                </button>
                                <span>{item.quantity}</span>
                                <button onClick={() => updatedItemQuantity(item.id, 1)}>
                                    +
                                </button>
                            </div>
                            <div className="image">
                                <img src={item.images[0].image_url}/>
                            </div>
                        </li>
                    );
                })}
                </ul>
            )}
            <p id="cart-total-price">
                Cart Total: <strong>{items[0].price.currency.symbol}{totalPrice}</strong>
            </p>
        </div>
    );
}

export default Cart;
