import { useContext } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../store/shopping-cart-context';

export default function ProductsList({ products }) {
  const { addItemToCart } = useContext(CartContext);

  const handleQuickShop = (product) => {
    const defaultAttributes = {};
    product.attributes.forEach((attributeSet) => {
      const setName = attributeSet.name;
      const firstAttribute = attributeSet.items[0];
      defaultAttributes[setName] = firstAttribute.value;
    });
    addItemToCart(product, defaultAttributes);
  };

  console.log(products)

  return (
    <div className="products-container">
      {products.map((product) => (
        <div className="product" key={product.id}>
          <div className={`inner-product ${!product.inStock ? 'not-in-stock' : ''}`}>
            <Link to={`${product.id}`}>
              <div className="image-container">
                <img src={product.images[0].image_url} alt={`${product.name}`} />
              </div>
              <div className="product-info">
                <p className="product-name">{product.name}</p>
                <p className="product-price">{product.price.currency.symbol}{product.price.amount}</p>
              </div>
            </Link>
            {product.inStock && (
              <button className="quick-shop" onClick={() => handleQuickShop(product)}>
                <i className="fa-solid fa-cart-shopping"></i>
              </button>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}
