import React, { useContext } from 'react';

import { CartContext } from '../store/shopping-cart-context';
import { groupAttributesBySet } from '../utilities/attributeUtils';
import AttributeSet from './AttributeSet';

function Cart() {
    const { items, updatedItemQuantity, handleCheckboxChange, allAttributesSelected, selectedAttributes } = useContext(CartContext);

    const totalPrice = items.reduce(
        (acc, item) => acc + item.price.amount * item.quantity, 0
    );
    
    return (
        <>
        <h2>{title}</h2>
        <div id="cart">
            {items.length === 0 && <p>No items in cart!</p>}
            {items.length > 0 && (
                <>
                <ul id="cart-items">
                {items.map((item) => {
                    const groupedAttributes = groupAttributesBySet(item.attributes)
                    return (
                        <li key={item.id}>
                            <div className="item-details">
                                <p className="item-name">{item.name}</p>
                                <p className="item-price">{item.price.currency.symbol}{item.price.amount}</p>
                                {Object.entries(groupedAttributes).map(([setName, attributes]) => (
                                    <AttributeSet
                                        key={setName}
                                        setName={setName}
                                        attributes={attributes}
                                        selectedAttributes={selectedAttributes}
                                        handleCheckboxChange={handleCheckboxChange}
                                  />
                                ))}
                            </div>
                            <div className="cart-item-actions">
                                <button onClick={() => updatedItemQuantity(item.id, -1)}>
                                    -
                                </button>
                                <span>{item.quantity}</span>
                                <button onClick={() => updatedItemQuantity(item.id, 1)}>
                                    +
                                </button>
                            </div>
                            <div className="image">
                                <img src={item.images[0].image_url}/>
                            </div>
                        </li>
                    );
                })}
                </ul>
                <p id="cart-total-price">
                    Cart Total: <strong>{items[0].price.currency.symbol}{totalPrice}</strong>
                </p>
                </>
            )}
        </div>
        </>
    );
}

export default Cart;
