import { useRouteLoaderData, json } from 'react-router-dom';

import Product from '../components/Product';

function ProductDetails() {
    const data = useRouteLoaderData('product-detail');

    return (
        <>
            <Product event={data.product} />
        </>
    );
}

export default ProductDetails; 

export async function loader({request, params}) {
    const id = params.productId;

    const response = await fetch(`http://localhost:8080/backend/getProductDetails.php?productId=${id}`);

    if(!response.ok) {
        throw json({message: 'Could not fetch details for selected product.'}, {status: 500});
    } else {
        return response;
    }
}
