export default function Product ({product}) {
    let attributeSets = product.attributes;
    let attributes = attributeSets.attributes;
    return (
        <div className="product">
            <div className="gallery">
                <div className="all-images">
        
                </div>
                <div className="main-image">
                    <img src={product.gallery_image_url} href={`${product.product_name} image`} />
                </div>
            </div>
            <div className="other-details">
                <h3>{product.product_name}</h3>
                <div className="attributeSets">
                    {Object.values(attributeSets).map((attributeSet, index) => (
                        <div className="attributeSet" key={index}>
                        <p>{attributeSet.attribute_set_name}</p>
                        {attributeSet.attributes.map((attribute) => (
                            <div className="attribute" key={attribute.attribute_id}>
                            {attribute.attribute_value}
                            </div>
                        ))}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
