import React, { Component } from 'react';
import { gql } from '@apollo/client';
import { client } from '../ApolloProvider'; // Ensure you import your Apollo client instance
import Product from '../components/Product';

const GET_PRODUCT_DETAILS = gql`
  query GetProductDetails($id: String!) {
    product(id: $id) {
      id
      name
      inStock
      description
      price {
        amount
        currency {
          symbol
        }
      }
      images {
        image_url
      }
      attributes {
        id
        value
        display_value
        attribute_set {
          name
        }
      }
    }
  }
`;

class ProductDetailsPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      error: null,
      product: null
    };
  }

  componentDidMount() {
    const { match } = this.props;
    const productId = match.params.productId; // Access productId from match.params
    client
      .query({
        query: GET_PRODUCT_DETAILS,
        variables: { id: productId }
      })
      .then(result => {
        this.setState({ loading: false, product: result.data.product });
      })
      .catch(error => {
        this.setState({ loading: false, error });
      });
  }

  render() {
    const { loading, error, product } = this.state;

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error.message}</div>;

    return (
      <div>
        <h2>Product Details</h2>
        <Product product={product} />
      </div>
    );
  }
}

export default ProductDetailsPage;
