import React from 'react';

const AttributeSet = ({ setName, attributes, selectedAttributes, handleCheckboxChange }) => {
  return (
    <div className="attribute-set" key={setName}>
      <h3 className="attribute-set-name">{setName}</h3>
      <div className="attributes">
        {attributes.map(attr => (
          <div key={attr.id} className="attribute">
            <label
              className={`inner-attribute ${selectedAttributes[setName] === attr.id ? 'selected' : ''}`}
              style={setName === 'Color' ? { backgroundColor: attr.value } : {}}
            >
              <input
                type="checkbox"
                onChange={() => handleCheckboxChange(setName, attr.id)}
                checked={selectedAttributes[setName] === attr.id}
              />
              {setName !== 'Color' && attr.display_value}
            </label>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AttributeSet;
