import React, { Component } from 'react';

class AttributeSet extends Component {
  render() {
    const { setName, attributes, selectedAttributes, handleCheckboxChange } = this.props;

    const toKebabCase = (str) =>
      str && str
        .match(/[A-Z]{2,}(?=[A-Z][a-z]+[0-9]*|\b)|[A-Z]?[a-z]+[0-9]*|[A-Z]{2,}|[0-9]+/g)
        .map((x) => x.toLowerCase())
        .join('-');
    

    return (
      <div className="attribute-set" key={setName}>
        <h3 className="attribute-set-name">{setName}</h3>
        <div className="attributes">
          {attributes.map(attr => (
            <div key={attr.id} className="attribute" data-testid={`cart-item-attribute-${toKebabCase(attr.name)}`}>
              <label
                className={`inner-attribute ${selectedAttributes[setName] === attr.id ? 'selected' : ''}`}
                style={setName === 'Color' ? { backgroundColor: attr.value } : {}}
              >
                <input
                  type="checkbox"
                  onChange={() => handleCheckboxChange(setName, attr.id)}
                  checked={selectedAttributes[setName] === attr.id}
                />
                {setName !== 'Color' && attr.display_value}
              </label>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default AttributeSet;
