export default function Product({ product }) {
    const attributeSets = product.attributes;
  
    return (
      <div className="product">
        <div className="gallery">
          <div className="all-images">
            {product.images && product.images.map((url, index) => (
              <img key={index} src={url} alt={`${product.product_name} image ${index + 1}`} />
            ))}
          </div>
          <div className="main-image">
            <img src={product.gallery_image_url} alt={`${product.product_name} main image`} />
          </div>
        </div>
        <div className="other-details">
          <h3>{product.product_name}</h3>
          <div className="attributeSets">
            {attributeSets && Object.values(attributeSets).map((attributeSet, index) => (
              <div className="attributeSet" key={index}>
                <p>{attributeSet.attribute_set_name}</p>
                {attributeSet.attributes && attributeSet.attributes.map((attribute, attrIndex) => (
                  <div className="attribute" key={attrIndex}>
                    {attribute.attribute_value}
                  </div>
                ))}
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
