import { NavLink } from 'react-router-dom';
import { useRef, useContext } from 'react';
import CartModal from './CartModal.js';
import { CartContext } from '../store/shopping-cart-context.js';

function MainNavigation() {
  const modal = useRef();
  const { items } = useContext(CartContext);

  const cartQuantity = items.length;

  function handleOpenCartClick() {
    modal.current.open();
  }

  let modalActions = <button onClick={() => modal.current.close()}>Close</button>;

  if (cartQuantity > 0) {
    modalActions = (
      <>
        <button onClick={() => modal.current.close()}>Close</button>
        <button>Checkout</button>
      </>
    );
  }

  return (
    <header>
      <nav>
        <ul>
          <li>
            <NavLink
              to="/clothes"
              className={({ isActive }) => (isActive ? 'active' : undefined)}
              end
            >
              Clothes
            </NavLink>
          </li>
          <li>
            <NavLink to="/tech" className={({ isActive }) => (isActive ? 'active' : undefined)}>
              Tech
            </NavLink>
          </li>
        </ul>
        <button id="cart" onClick={handleOpenCartClick}>
          <i className="fa-solid fa-cart-shopping"></i>
        </button>
        <CartModal ref={modal} title="My Bag" actions={modalActions} />
      </nav>
    </header>
  );
}

export default MainNavigation;
